import React from 'react';
import ReactDOM from 'react-dom';


charactersArray=["images/stewie.jpg", "lois.jpg"]
// const charactersLis = characters.map((character, i ) =>
//   // expression goes here:
// 	<img key={'character_' + i}> { character } </img>
// );

function changeImage(e) {
  const rand = Math.random() < 0.5;
  e.target.setAttribute('src', charactersArray[0]);
}


function Card(props) {
  
    return (
      <div className="card text-center">
        <div className="card-body">
          <button className="btn">

            <img 
            src={'stewie.jpg'}
            width={'auto'} 
            height={'200px'} 
            onClick={changeImage}/>

          </button>
        </div>
      </div>
    );
  }

  

export default Card;
