    this.setState({ images });
  };


  render() {
    return (
      <div className="main">
        <div className="header">
          <Header />
          <Jumbotron />
        </div>
        <div className="container">
          {this.state.images.map(image => (
            <button className="btn" onClick={this.updateImage}>
              <Card
                removeImage={this.removeImage}
                id={image.id}
                key={image.id}
                name={image.name}
                image={image.src}
                width={image.width}
                height={image.height}
                alt={image.alt}
              />
            </button>
          ))}  
        </div>
      </div>
    )
  }
}

export default App;
