import React from 'react';
import ReactDOM from 'react-dom';

// import logo from './logo.png';
const characters =["stewie.jpg", "https://s3.amazonaws.com/codecademy-content/courses/React/react_photo-puppy.jpeg"];

 
const charactersLis = characters.map((character, i ) =>
  // expression goes here:
	<img key={'character_' + i}> { character } </img>
);

function changeImage(character) {
  const rand = Math.random([0]) < 0.5;
  character.target.setAttribute('src', rand(characters));
}


function Card(props) {
  // const characters =["stewie.jpg", "https://s3.amazonaws.com/codecademy-content/courses/React/react_photo-puppy.jpeg"];
  
    return (
      <div className="card text-center">
        <div className="card-body">
          <button className="btn">

            <img 
            src={'images/stewie.jpg'}
            width={'auto'} 
            height={'200px'} 
            onClick={changeImage}/>

          </button>
        </div>
      </div>
    );
  }

  

export default Card;
